    @Test
    fun threadOrderTest() =
        runBlocking {
            val eventArray =
                JacksonMapper.mapper.readValue<List<Event>>(db) + Event.fromJson(header)

            var counter = 0
            eventArray.forEach {
                TestCase.assertTrue("${it.id} failed signature check", it.verify())
                LocalCache.justConsume(it, null, false)
                counter++
            }

            val naddr =
                ATag(
                    30023,
                    "6e468422dfb74a5738702a8823b9b28168abab8655faacb6853cd0ee15deee93",
